///////////////////////////////////////////////////////////////////// IMPORTS
import View from "./view";
import icons from "../../img/icons.svg"; // DOES => Imports icons to be used in markup

///////////////////////////////////////////////////////////////////// PREVIEW VIEW
class PreviewView extends View {
	//////////////////////////////// PROTECTED METHODS
	_parentElement = "";

	//////////////////////////////////////////////////// GENERATE MARKUP
	// FUNCTIONALITY => Generates markup getting data from search results and looping through all of them
	_generateMarkup() {
		const id = window.location.hash.slice(1);

		return `
      <li class="preview">
        <a class="preview__link ${
					this._data.id === id ? "preview__link--active" : ""
				}" href="#${this._data.id}">
          <figure class="preview__fig">
            <img src="${this._data.image}" alt="Preview" />
          </figure>
          <div class="preview__data">
            <h4 class="preview__title">${this._data.title}</h4>
            <p class="preview__publisher">${this._data.publisher}</p>
            <div class="preview__user-generated ${
							this._data.key ? "" : "hidden"
						}">
              <svg>
              <use href="${icons}#icon-user"></use>
              </svg>
            </div>
          </div>
        </a>
      </li>
    `;
	}
}

export default new PreviewView();
